import React, { useState } from 'react';
import axios from 'axios';

const WishlistForm = () => {
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');

        // Frontend validation for email format
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(email)) {
            setMessage('Please enter a valid email address.');
            return;
        }

        try {
            // Send email to backend API
            const response = await axios.post('http://localhost:5000/api/emails', { email });
            setMessage(response.data.message);
            setEmail('');
        } catch (error) {
            setMessage(error.response?.data?.message || 'Something went wrong. Please try again.');
        }
    };

    return (
        <div style={{ padding: '20px', textAlign: 'center' }}>
            <h1>Be the First to Know About Our Launch!</h1>
            <p>Sign up to stay updated and get exclusive early-bird offers!</p>
            <form onSubmit={handleSubmit}>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Enter your email"
                    required
                    style={{ padding: '10px', width: '250px' }}
                />
                <button type="submit" style={{ padding: '10px 20px', marginLeft: '10px' }}>
                    Join Wishlist
                </button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default WishlistForm;
